CREATE TABLE animal (
    `id` NUMERIC(3, 1),
    `name_animal` VARCHAR(18) CHARACTER SET utf8,
    `diet_animal` VARCHAR(21) CHARACTER SET utf8,
    `status_animal` VARCHAR(36) CHARACTER SET utf8,
    `family_animal` VARCHAR(9) CHARACTER SET utf8,
    `population_animal` VARCHAR(23) CHARACTER SET utf8,
    `description_animal` VARCHAR(26) CHARACTER SET utf8,
    `nickname_animal` VARCHAR(78) CHARACTER SET utf8,
    `main_picture_animal` VARCHAR(16) CHARACTER SET utf8
);
